function sniffer() {
  let videoTags = document.getElementsByTagName("video");
  let urls = [];
  if (videoTags.length > 0) {
    for (var i = 0; i < videoTags.length; i++) {
      let url = videoTags[i].src;
      if (url != null && url.length > 0) {
        urls.push(url);
      }
    }
    window.appInterface.onSnifferVideo(videoTags.length, urls);
  } else {
    window.appInterface.onSnifferVideo(0, urls);
  }
}
